mapper.select:
select * from sys_org where delete_flag=1

    and   left_node >=#{left}


      and   right_node <= #{right}


      and   level=#{level}

mapper.update:
update sys_org set left_node=left_node+#{count}
where left_node>=#{leftNum}
and delete_flag=1

update sys_org set right_node=right_node+#{count}
where right_node>=#{rightNum}
and delete_flag=1

mapper.select:
select * from sys_org where right_node in (
select max(right_node) from sys_org where delete_flag=1
) and delete_flag=1
